@Test
    public void canonicalVersionShouldNotConatainSnapshotWhenVersionIsSnapshot()
    {
        assertThat( VersionFormatter.asCanonicalVersion( "1.0-SNAPSHOT", "201009291818", "1" ),
                    not( containsString( "SNAPSHOT" ) ) );
    }

