        Error.push("compositeMetadata");
        try {
            CompositeMetadata cmd = buildCompositeMetadata(root, gmd, new Path(), null, new MutablePath());
            return cmd;
        } finally {
            LOGGER.debug("end buildCompositeMetadata");
            Error.pop();
        }
    }
