@Test
    public void testGetLocale() {
        Locale.setDefault(Locale.GERMANY);
        Assert.assertEquals(Locale.GERMANY, I18N.getLocale());
        Locale.setDefault(Locale.JAPAN);
        Assert.assertEquals(Locale.JAPAN, I18N.getLocale());
    }

