@Test
  public void testGetColor()
  {
    final UserSettingsImpl settings = new UserSettingsImpl( "foo" );

    settings.putColor( "key", Color.WHITE );
    assertEquals( Color.WHITE, settings.getColor( "key", Color.BLACK ) );
  }

