String createRelativePath(String baseURI, String path) {
        int lastPathIdx = baseURI.lastIndexOf('/');
        if (lastPathIdx > 0) {
            return baseURI.substring(0, lastPathIdx + 1) + path;
        } else {
            return path;
        }
    }
