@Test
    public void urlAsStringViaFactoryMethod()
    {
        assertThat(
            "Scan features url",
            scanFeatures(
                "file:foo-features.xml", "f1", "f2/1.0.0"
            ).getURL(),
            is( equalTo( "scan-features:file:foo-features.xml!/f1,f2/1.0.0" ) )
        );
    }

