public String getURL()
    {
        final StringBuilder url = new StringBuilder()
            .append( SCHEMA )
            .append( SEPARATOR_SCHEME )
            .append( super.getURL() )
            .append( SEPARATOR_FILTER );
        boolean first = true;
        for( String feature : m_features )
        {
            if( !first )
            {
                url.append( FEATURE_SEPARATOR );
            }
            first = false;
            url.append( feature );
        }
        url.append( getOptions( this ) );
        return url.toString();
    }
