@Test
    public void testFilter() {
        //filter odd
        CloseableIterator<Integer> odd = filter(testIterator(), input -> input % 2 == 0);

        assertTrue(elementsEqual(asList(2, 4).iterator(), odd));
        assertFalse(odd.hasNext());
        odd.close();
    }

